def implementation_one(input: str, window_length: int):
    """
    This is a dumb implementation that just checks that all letters are unique
    by building a set for them and checking the length is 4 (which means no duplicates)
    and if it fails, it steps forward one.
    """
    idx = window_length
    input_length = len(input)
    while idx < input_length:
        if len(set(input[idx - window_length : idx])) == window_length:
            print(idx)
            return

        idx += 1

    print("Nope")


